mapper.update:
INSERT INTO task_detail (
kid, task_id, invoices_code, item_code, item_name, item_total, item_storage_code
) VALUES
(
#{item.kid}, #{item.task_id}, #{item.invoices_code}, #{item.item_code}, #{item.item_name}, #{item.item_total}, #{item.item_storage_code}
)

mapper.select:
select td.invoices_code,td.item_code,td.item_name,td.item_total,td.item_storage_code,sp.point from task_detail td
left join storage_point sp on sp.item_storage_code = td.item_storage_code
where task_id = #{kid}
